import React, { useEffect, useState } from 'react'
import styled from 'styled-components';
import Item from '../components/Item';
import axios from 'axios';

const Container = styled.div`
    width: 100vw;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
`

export default function Items() {
    const [responseData, setResponseData] = useState('');
    const [state, setState] = useState('');

    const handleState = () => {
        if(document.readyState === 'loading'){
            setState('loading');
        } else if (document.readyState === 'complete'){
            setState('complete');
        } else setState('intaractive');
    }

    handleState();

    useEffect(() => {
       const handleRequest = () => {
        const options = {
            method: 'GET',
            url: 'https://the-earth-science-online-video-database.p.rapidapi.com/videos',
            headers: {
              'X-RapidAPI-Key': '7015901614mshbeaa572245b6bffp123fd4jsn9b3a74df405d',
              'X-RapidAPI-Host': 'the-earth-science-online-video-database.p.rapidapi.com'
            }
        }

        axios.request(options).then(function (response) {
            console.log(response.data);
            setResponseData(response.data)
        }).catch(function (error) {
            console.error(error);
        });
       }
    }, [state])
  return (
    <Container>
        {responseData && responseData.map(item =>{return(<Item data={item}/>)} )}
    </Container>
  )
}
